let kategoriDalam = null, kategoriLuar = null;
let i = 0;
const keys = Object.keys(anggotaHumi);

// Menghitung kategori untuk kelembaban dalam ruangan
while (i < keys.length) {
    const namaKategori = keys[i];
    const batasBawah = anggotaHumi[namaKategori].batasBawah;
    const batasAtas = anggotaHumi[namaKategori].batasAtas;

    if (humiDalam >= batasBawah && humiDalam <= batasAtas) {
        kategoriDalam = namaKategori;
        break;
    }
    i++;
}

